import numpy as np

def get_matrix_and_nodes(distance_matrix, local_opt_tour, global_opt_tour, edge_n_used):
    edges_not_used_frequently = np.zeros(distance_matrix.shape)
    for i in range(len(edge_n_used)):
        for j in range(len(edge_n_used[i])):
            if edge_n_used[i][j] < 2:
                edges_not_used_frequently[i][j] = 1

    perturb_impact = np.sum(edges_not_used_frequently, axis=1)
    new_distance_matrix = distance_matrix + (1 / (edge_n_used + 1)) * edges_not_used_frequently
    perturb_nodes = np.argsort(perturb_impact)[::-1]

    return new_distance_matrix, perturb_nodes
