import numpy as np

def update_edge_distance(edge_distance, local_opt_tour, edge_n_used):
    updated_edge_distance = np.copy(edge_distance)

    max_edge_usage = np.max(edge_n_used)
    for i in range(len(local_opt_tour)):
        city1 = local_opt_tour[i]
        city2 = local_opt_tour[(i + 1) % len(local_opt_tour)]
        if edge_n_used[city1, city2] == max_edge_usage:
            updated_edge_distance[city1, city2] *= 1.5

    return updated_edge_distance

