import numpy as np

def update_edge_distance(edge_distance, local_opt_tour, edge_n_used):
    updated_edge_distance = np.copy(edge_distance)
    max_edges = np.argmax(updated_edge_distance, axis=1)
    for i in range(len(local_opt_tour) - 1):
        start = local_opt_tour[i]
        end = local_opt_tour[i + 1]
        if updated_edge_distance[start][end] == np.max(updated_edge_distance[max_edges[start]]):
            updated_edge_distance[start][end] += 0.5  # Add a constant value to encourage exploration
    return updated_edge_distance
