import numpy as np

def update_edge_distance(edge_distance, local_opt_tour, edge_n_used):
    updated_edge_distance = np.copy(edge_distance)

    edge_average_distance = np.zeros_like(edge_distance)
    for i in range(len(local_opt_tour)):
        start = local_opt_tour[i]
        for j in range(len(local_opt_tour)):
            end = local_opt_tour[j]
            edge_average_distance[start][end] += edge_distance[start][end]
    edge_average_distance /= len(local_opt_tour)

    for i in range(edge_distance.shape[0]):
        for j in range(edge_distance.shape[1]):
            updated_edge_distance[i][j] += edge_average_distance[i][j]

    return updated_edge_distance
