            if edge_count[i][j] > 0:
                penalty_factor = 0.1 * edge_count[i][j] / (edge_n_used[i][j]+1)  # Introduce penalty for frequently used edges
                updated_edge_distance[i][j] += penalty_factor * edge_distance[i][j]

